class myThread(QThread):
	sig = pyqtSignal(str)
    
	def run(self):
                global flag
                while True:
                    if flag:
                        data = ser.read(ser.in_waiting).hex()
                        self.sig.emit(data)
                        print("--------------------")
                        print(data)

    

class mainWin(QWidget):
    def __init__(self):
        super().__init__()
        self.initUI()
        
    def initUI(self):
        self.resize(400, 300)
        self.setWindowTitle('test')
        layout = QVBoxLayout()
        self.btn = QPushButton('test', self)
        self.btn.clicked.connect(self.test)
        self.text = QTextEdit(self)
        layout.addWidget(self.btn)
        layout.addWidget(self.text)
        self.setLayout(layout)
        thread = myThread()
        thread.sig.connect(self.textAppend)
        thread.start()
        
    def test(self):
        global flag
        if flag:
            flag = False
        else:
            flag = True
    
    def textAppend(self, data):
        self.text.append(data)


if __name__ == '__main__':
    global flag, ser
    flag = False
    ser = serial.Serial(  # 下面这些参数根据情况修改
        port='COM2',  # 串口
        baudrate=4608000,  # 波特率
        stopbits=1,
        bytesize=8)
    app = QApplication(sys.argv)
    win = mainWin()
    win.show()
    sys.exit(app.exec_())

    
    

